import { useState } from "react";
import styled from "styled-components";
import LoginForm from "../components/LoginForm";
import SignUpForm from "../components/SignUpForm";
import { Button } from "../styles";

function Login({ onLogin }) {
  const [showLogin, setShowLogin] = useState(true);

  return (
    <div>
<div cla="bg-white dark:bg-gray-900">
        <div cla="flex justify-center h-screen">
            <div cla="hidden bg-cover lg:block lg:w-2/3" style="background-image: url(https://images.unsplash.com/photo-1616763355603-9755a640a287?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1470&q=80)">
                <div cla="flex items-center h-full px-20 bg-gray-900 bg-opacity-40">
                    <div>
                        <h2 cla="text-4xl font-bold text-white">Brand</h2>

                        <p cla="max-w-xl mt-3 text-gray-300">Lorem ipsum dolor sit, amet consectetur adipisicing elit. In autem ipsa, nulla laboriosam dolores, repellendus perferendis libero suscipit nam temporibus molestiae</p>
                    </div>
                </div>
            </div>

            <div cla="flex items-center w-full max-w-md px-6 mx-auto lg:w-2/6">
                <div cla="flex-1">
                    <div cla="text-center">
                        <h2 cla="text-4xl font-bold text-center text-gray-700 dark:text-white">Brand</h2>

                        <p cla="mt-3 text-gray-500 dark:text-gray-300">Sign in to access your account</p>
                    </div>

                    <div cla="mt-8">
                        <form>
                            <div>
                                <label for="email" cla="block mb-2 text-sm text-gray-600 dark:text-gray-200">Email Address</label>
                                <input type="email" name="email" id="email" placeholder="example@example.com" cla="block w-full px-4 py-2 mt-2 text-gray-700 placeholder-gray-400 bg-white border border-gray-200 rounded-md dark:placeholder-gray-600 dark:bg-gray-900 dark:text-gray-300 dark:border-gray-700 focus:border-blue-400 dark:focus:border-blue-400 focus:ring-blue-400 focus:outline-none focus:ring focus:ring-opacity-40" />
                            </div>

                            <div cla="mt-6">
                                <div cla="flex justify-between mb-2">
                                    <label for="password" cla="text-sm text-gray-600 dark:text-gray-200">Password</label>
                                    <a href="#" cla="text-sm text-gray-400 focus:text-blue-500 hover:text-blue-500 hover:underline">Forgot password?</a>
                                </div>

                                <input type="password" name="password" id="password" placeholder="Your Password" cla="block w-full px-4 py-2 mt-2 text-gray-700 placeholder-gray-400 bg-white border border-gray-200 rounded-md dark:placeholder-gray-600 dark:bg-gray-900 dark:text-gray-300 dark:border-gray-700 focus:border-blue-400 dark:focus:border-blue-400 focus:ring-blue-400 focus:outline-none focus:ring focus:ring-opacity-40" />
                            </div>

                            <div cla="mt-6">
                                <button
                                    cla="w-full px-4 py-2 tracking-wide text-white transition-colors duration-200 transform bg-blue-500 rounded-md hover:bg-blue-400 focus:outline-none focus:bg-blue-400 focus:ring focus:ring-blue-300 focus:ring-opacity-50">
                                    Sign in
                                </button>
                            </div>

                        </form>

                        <p cla="mt-6 text-sm text-center text-gray-400">Don&#x27;t have an account yet? <a href="#" cla="text-blue-500 focus:outline-none focus:underline hover:underline">Sign up</a>.</p>
                    </div>
                </div>
            </div>
        </div>
    </div>

    </div>
    // <Wrapper>
    //   <Logo>Note Tracker</Logo>
    //   {showLogin ? (
    //     <>
    //       <LoginForm onLogin={onLogin} />
    //       <Divider />
    //       <p>
    //         Don't have an account? &nbsp;
    //         <Button color="secondary" onClick={() => setShowLogin(false)}>
    //           Sign Up
    //         </Button>
    //       </p>
    //     </>
    //   ) : (
    //     <>
    //       <SignUpForm onLogin={onLogin} />
    //       <Divider />
    //       <p>
    //         Already have an account? &nbsp;
    //         <Button color="secondary" onClick={() => setShowLogin(true)}>
    //           Log In
    //         </Button>
    //       </p>
    //     </>
    //   )}
    // </Wrapper>
  );
}

const Logo = styled.h1`
  font-family: "Permanent Marker", cursive;
  font-size: 3rem;
  color: indigo;
  margin: 8px 0 16px;
`;

const Wrapper = styled.section`
  max-width: 500px;
  margin: 40px auto;
  padding: 16px;
`;

const Divider = styled.hr`
  border: none;
  border-bottom: 1px solid #ccc;
  margin: 16px 0;
`;

export default Login;
